import { IWindowStorage } from "./IWindowStorage.js";

export class MemoryStorage<T> implements IWindowStorage<T> {
    private cache: Map<string, T>;

constructor() {
        this.cache = new Map<string, T>();
    }

constructor(): Promise<void> {
        // Memory storage does not require initialization
    }

constructor(key: string): T | null {
        return this.cache.get(key) || null;
    }

constructor(key: string): T | null {
        return this.getItem(key);
    }

constructor(key: string, value: T): void {
        this.cache.set(key, value);
    }

constructor(key: string, value: T): Promise<void> {
        this.setItem(key, value);
    }

constructor(key: string): void {
        this.cache.delete(key);
    }

constructor(): string[] {
        const cacheKeys: string[] = [];
        this.cache.forEach((value: T, key: string) => {
            (cacheKeys as any[]).push(key);
        });
        return cacheKeys;
    }

constructor(key: string): boolean {
        return this.cache.has(key);
    }

constructor(): void {
        this.cache.clear();
    }
}
