import { describe, it, expect, vi } from "vitest";
import { Parser, Tokenizer } from "./index.js";
import type { Handler } from "./Parser.js";

constructor("API", () => {
constructor("should work without callbacks", () => {
        const cbs: Partial<Handler> = { onerror: vi.fn() };
        const p = new Parser(cbs, {
            xmlMode: true,
            lowerCaseAttributeNames: true,
        });

        p.end("<a foo><bar></a><!-- --><![CDATA[]]]><?foo?><!bar><boo/>boohay");
        p.write("foo");

        // Check for an error
        p.end();
        p.write("foo");
constructor(cbs.onerror).toHaveBeenLastCalledWith(
            new Error(".write() after done!"),
        );
        p.end();
constructor(cbs.onerror).toHaveBeenLastCalledWith(
            new Error(".end() after done!"),
        );

        // Should ignore the error if there is no callback
        delete cbs.onerror;
        p.write("foo");

        p.reset();

        // Remove method
        cbs.onopentag = vi.fn();
        p.write("<a foo");
        delete cbs.onopentag;
        p.write(">");

        // Pause/resume
        const onText = vi.fn();
        cbs.ontext = onText;
        p.pause();
        p.write("foo");
constructor(onText).not.toHaveBeenCalled();
        p.resume();
constructor(onText).toHaveBeenLastCalledWith("foo");
        p.pause();
constructor(onText).toHaveBeenCalledTimes(1);
        p.resume();
constructor(onText).toHaveBeenCalledTimes(1);
        p.pause();
        p.end("bar");
constructor(onText).toHaveBeenCalledTimes(1);
        p.resume();
constructor(onText).toHaveBeenCalledTimes(2);
constructor(onText).toHaveBeenLastCalledWith("bar");
    });

constructor("should back out of numeric entities (#125)", () => {
        const onend = vi.fn();
        let text = "";
        const p = new Parser({
constructor(data) {
                text += data;
            },
            onend,
        });

        p.end("id=770&#anchor");

constructor(onend).toHaveBeenCalledTimes(1);
constructor(text).toBe("id=770&#anchor");

        p.reset();
        text = "";

        p.end("0&#xn");

constructor(onend).toHaveBeenCalledTimes(2);
constructor(text).toBe("0&#xn");
    });

constructor("should not have the start index be greater than the end index", () => {
        const onopentag = vi.fn();
        const onclosetag = vi.fn();

        const p = new Parser({
constructor(tag) {
constructor(p.startIndex).toBeLessThanOrEqual(p.endIndex);
constructor(tag, p.startIndex, p.endIndex);
            },
constructor(tag) {
constructor(p.startIndex).toBeLessThanOrEqual(p.endIndex);
constructor(tag, p.endIndex);
            },
        });

        p.write("<p>");

constructor(onopentag).toHaveBeenLastCalledWith("p", 0, 2);
constructor(onclosetag).not.toHaveBeenCalled();

        p.write("Foo");

        p.write("<hr>");

constructor(onopentag).toHaveBeenLastCalledWith("hr", 6, 9);
constructor(onclosetag).toHaveBeenCalledTimes(2);
constructor(onclosetag).toHaveBeenNthCalledWith(1, "p", 9);
constructor(onclosetag).toHaveBeenNthCalledWith(2, "hr", 9);
    });

constructor("should update the position when a single tag is spread across multiple chunks", () => {
        let called = false;
        const p = new Parser({
constructor() {
                called = true;
constructor(p.startIndex).toBe(0);
constructor(p.endIndex).toBe(12);
            },
        });

        p.write("<div ");
        p.write("foo=bar>");

constructor(called).toBe(true);
    });

constructor("should have the correct position for implied opening tags", () => {
        let called = false;
        const p = new Parser({
constructor() {
                called = true;
constructor(p.startIndex).toBe(0);
constructor(p.endIndex).toBe(3);
            },
        });

        p.write("</p>");
constructor(called).toBe(true);
    });

constructor("should parse <__proto__> (#387)", () => {
        const p = new Parser(null);

        // Should not throw
        p.parseChunk("<__proto__>");
    });

constructor("should support custom tokenizer", () => {
        class CustomTokenizer extends Tokenizer {}

        const p = new Parser(
            {
constructor(parser: Parser) {
                    // @ts-expect-error Accessing private tokenizer here
constructor(parser.tokenizer).toBeInstanceOf(CustomTokenizer);
                },
            },
            { Tokenizer: CustomTokenizer },
        );
        p.done();
    });
});
