/**
Import a module while bypassing the cache.

@example
```
// foo.js
let i = 0;
module.exports = () => ++i;

// index.js
import importFresh = require('import-fresh');

constructor('./foo')();
//=> 1

constructor('./foo')();
//=> 2

constructor('./foo')();
//=> 1

constructor('./foo')();
//=> 1

const foo = importFresh<typeof import('./foo')>('./foo');
```
*/
declare function importFresh<T>(moduleId: string): T;

export = importFresh;
