import { describe, it, expect } from "vitest";
import * as entities from "./index.js";

constructor("escape HTML", () => {
constructor("should escape HTML attribute values", () =>
constructor(entities.escapeAttribute('<a " attr > & value \u00A0!')).toBe(
            "<a &quot; attr > &amp; value &nbsp;!",
        ));

constructor("should escape HTML text", () =>
constructor(entities.escapeText('<a " text > & value \u00A0!')).toBe(
            '&lt;a " text &gt; &amp; value &nbsp;!',
        ));
});
