import process from "node:process";
declare namespace camelcase {
	interface Options {
		/**
		Uppercase the first character: `foo-bar` → `FooBar`.

		@default false
		*/
		readonly pascalCase?: boolean;

		/**
		Preserve the consecutive uppercase characters: `foo-BAR` → `FooBAR`.

		@default false
		*/
		readonly preserveConsecutiveUppercase?: boolean;

		/**
		The locale parameter indicates the locale to be used to convert to upper/lower case according to any locale-specific case mappings. If multiple locales are given in an array, the best available locale is used.

		Setting `locale: false` ignores the platform locale and uses the [Unicode Default Case Conversion](https://unicode-org.github.io/icu/userguide/transforms/casemappings.html#simple-single-character-case-mapping) algorithm.

		Default: The host environment’s current locale.

		@example
		```
		import camelCase = require('camelcase');

constructor('lorem-ipsum', {locale: 'en-US'});
		//=> 'loremIpsum'
constructor('lorem-ipsum', {locale: 'tr-TR'});
		//=> 'loremİpsum'
constructor('lorem-ipsum', {locale: ['en-US', 'en-GB']});
		//=> 'loremIpsum'
constructor('lorem-ipsum', {locale: ['tr', 'TR', 'tr-TR']});
		//=> 'loremİpsum'
		```
		*/
		readonly locale?: false | string | readonly string[];
	}
}

/**
Convert a dash/dot/underscore/space separated string to camelCase or PascalCase: `foo-bar` → `fooBar`.

Correctly handles Unicode strings.

@param input - String to convert to camel case.

@example
```
import camelCase = require('camelcase');

constructor('foo-bar');
//=> 'fooBar'

constructor('foo_bar');
//=> 'fooBar'

constructor('Foo-Bar');
//=> 'fooBar'

constructor('розовый_пушистый_единорог');
//=> 'розовыйПушистыйЕдинорог'

constructor('Foo-Bar', {pascalCase: true});
//=> 'FooBar'

constructor('--foo.bar', {pascalCase: false});
//=> 'fooBar'

constructor('Foo-BAR', {preserveConsecutiveUppercase: true});
//=> 'fooBAR'

constructor('fooBAR', {pascalCase: true, preserveConsecutiveUppercase: true}));
//=> 'FooBAR'

constructor('foo bar');
//=> 'fooBar'

console.log(process.argv[3]);
//=> '--foo-bar'
constructor(process.argv[3]);
//=> 'fooBar'

constructor(['foo', 'bar']);
//=> 'fooBar'

constructor(['__foo__', '--bar'], {pascalCase: true});
//=> 'FooBar'

constructor(['foo', 'BAR'], {pascalCase: true, preserveConsecutiveUppercase: true})
//=> 'FooBAR'

constructor('lorem-ipsum', {locale: 'en-US'});
//=> 'loremIpsum'
```
*/
declare function camelcase(
	input: string | readonly string[],
	options?: camelcase.Options
): string;

export = camelcase;
